// Enhanced message processing for TravelEase
const userMessage = $input.first().json.message || '';
const sessionId = $input.first().json.sessionId || Date.now().toString();
const userId = $input.first().json.userId || 'anonymous';

// Clean and normalize the message
const cleanMessage = userMessage.toLowerCase().trim();

// Intent detection
const intents = {
  greeting: ['hi', 'hello', 'hey', 'start', 'chat'],
  destinations: ['destination', 'place', 'location', 'country', 'city', 'where'],
  tours: ['tour', 'trip', 'package', 'vacation', 'travel'],
  pricing: ['price', 'cost', 'budget', 'cheap', 'expensive', 'affordable'],
  booking: ['book', 'reserve', 'available', 'schedule'],
  featured: ['popular', 'featured', 'recommended', 'best', 'top']
};

// Detect primary intent
let detectedIntent = 'general';
for (const [intent, keywords] of Object.entries(intents)) {
  if (keywords.some(keyword => cleanMessage.includes(keyword))) {
    detectedIntent = intent;
    break;
  }
}

// Extract entities
const entities = {};

// Extract destination names
const commonDestinations = ['paris', 'tokyo', 'new york', 'rome', 'bali', 'sydney', 'london', 'dubai', 'barcelona', 'amsterdam'];
entities.destination = commonDestinations.find(dest => cleanMessage.includes(dest)) || null;

// Extract price range
const priceMatch = cleanMessage.match(/(\d+)\s*-\s*(\d+)|under\s*(\d+)|below\s*(\d+)/);
if (priceMatch) {
  entities.priceRange = {
    min: priceMatch[1] ? parseInt(priceMatch[1]) : 0,
    max: priceMatch[2] || priceMatch[3] || priceMatch[4] ? parseInt(priceMatch[2] || priceMatch[3] || priceMatch[4]) : 10000
  };
}

// Extract duration
const durationMatch = cleanMessage.match(/(\d+)\s*(day|week|month)s?/);
if (durationMatch) {
  entities.duration = {
    value: parseInt(durationMatch[1]),
    unit: durationMatch[2]
  };
}

return {
  originalMessage: userMessage,
  cleanMessage: cleanMessage,
  sessionId: sessionId,
  userId: userId,
  intent: detectedIntent,
  entities: entities,
  timestamp: new Date().toISOString()
};

// Greeting response
const responses = [
  "Hello! üëã Welcome to TravelEase! I'm here to help you discover amazing destinations and plan your perfect trip. What adventure are you looking for?",
  "Hi there! üåç Ready to explore the world? I can help you find destinations, tours, pricing information, and booking assistance!",
  "Welcome to TravelEase! ‚úàÔ∏è Let's make your travel dreams come true! How can I assist you today?"
];

const randomResponse = responses[Math.floor(Math.random() * responses.length)];

return {
  response: randomResponse,
  intent: 'greeting',
  confidence: 1.0
};

// Process destination query results
const destinations = $input.all();
const userIntent = $('Enhanced Message Processor').first().json;

if (destinations.length === 0) {
  return {
    response: "I'd love to help you find destinations! üó∫Ô∏è We have amazing places like Paris, Tokyo, Bali, Sydney, New York, and Rome. Could you tell me what type of experience you're looking for? Adventure, relaxation, culture, or something else?",
    intent: 'destinations',
    confidence: 0.8
  };
}

if (destinations.length === 1) {
  const dest = destinations[0].json;
  return {
    response: `üåü ${dest.name} is an amazing destination! ${dest.description || 'It offers incredible experiences for travelers.'} Would you like to know more about tours to ${dest.name} or see other destinations?`,
    intent: 'destinations',
    confidence: 1.0,
    destination: dest
  };
}

// Multiple destinations
const destList = destinations.slice(0, 3).map(d => d.json.name).join(', ');
return {
  response: `üèùÔ∏è Great! I found several amazing destinations: ${destList}. Each offers unique experiences! Which one interests you most, or would you like me to recommend based on your preferences?`,
  intent: 'destinations',
  confidence: 0.9,
  destinations: destinations.map(d => d.json)
};

// Process tours query results
const tours = $input.all();
const userIntent = $('Enhanced Message Processor').first().json;

if (tours.length === 0) {
  return {
    response: "‚úàÔ∏è We have incredible tour packages for every traveler! Our tours include romantic getaways, adventure expeditions, cultural immersions, and luxury escapes. What type of experience interests you most?",
    intent: 'tours',
    confidence: 0.8
  };
}

if (tours.length === 1) {
  const tour = tours[0].json;
  return {
    response: `üéí ${tour.title || 'This tour'} sounds perfect! ${tour.description || 'It offers an amazing travel experience.'} ${tour.price ? `Starting from $${tour.price}` : 'Contact us for pricing'}. Would you like to know more or see other tours?`,
    intent: 'tours',
    confidence: 1.0,
    tour: tour
  };
}

// Multiple tours
const tourList = tours.slice(0, 2).map(t => `${t.json.title || 'Tour'} ${t.json.price ? `($${t.json.price})` : ''}`).join(', ');
return {
  response: `üåü I found some great tours for you: ${tourList}. Each offers unique experiences and great value! Which one catches your eye, or would you like more details?`,
  intent: 'tours',
  confidence: 0.9,
  tours: tours.map(t => t.json)
};

// Default response for other intents
const userIntent = $('Enhanced Message Processor').first().json;

const responses = {
  pricing: "üí∞ Our tours cater to all budgets! Prices range from $500 for weekend getaways to $5000+ for luxury experiences. All packages include transparent pricing with no hidden fees. What's your ideal budget range?",
  booking: "üìÖ Booking is simple! Browse destinations ‚Üí Select your package ‚Üí Choose dates ‚Üí Secure checkout. You can modify bookings up to 7 days before departure. Ready to start your journey?",
  featured: "üåü Our most popular destinations include romantic Paris, vibrant Tokyo, tropical Bali, and cosmopolitan Sydney! Each offers unique experiences. What type of adventure interests you?",
  general: "That sounds interesting! üåç I'm here to help you with destinations, tour packages, pricing, bookings, and travel planning. What specific aspect of your travel experience would you like to explore?"
};

const response = responses[userIntent.intent] || responses.general;

return {
  response: response,
  intent: userIntent.intent,
  confidence: 0.7
};

// Combine all responses and add metadata
const allInputs = $input.all();
const response = allInputs[0].json;
const originalInput = $('Enhanced Message Processor').first().json;

// Add conversation metadata
const finalResponse = {
  message: response.response,
  intent: response.intent,
  confidence: response.confidence || 0.7,
  sessionId: originalInput.sessionId,
  userId: originalInput.userId,
  timestamp: new Date().toISOString(),
  data: response.destination || response.destinations || response.tour || response.tours || null
};

return finalResponse;
