// Enhanced TravelEase Chat Agent Response Generator
const inputData = $input.first().json;
const message = (inputData.message || '').toLowerCase();

// Get data from MongoDB queries
let tours = [];
let destinations = [];

try {
  tours = $('Query Tours').all().map(item => item.json) || [];
} catch (e) {
  console.log('No tours data available');
}

try {
  destinations = $('Query Destinations').all().map(item => item.json) || [];
} catch (e) {
  console.log('No destinations data available');
}

// Intent detection patterns
const intents = {
  tours: ['tour', 'trip', 'package', 'vacation', 'travel'],
  destinations: ['destination', 'place', 'location', 'country', 'city'],
  pricing: ['price', 'cost', 'budget', 'cheap', 'expensive', 'affordable'],
  featured: ['popular', 'featured', 'recommended', 'best', 'top']
};

// Detect user intent
function detectIntent(msg) {
  const detectedIntents = [];
  for (const [intent, keywords] of Object.entries(intents)) {
    if (keywords.some(keyword => msg.includes(keyword))) {
      detectedIntents.push(intent);
    }
  }
  return detectedIntents.length > 0 ? detectedIntents : ['general'];
}

// Extract price range from message
function extractPriceRange(msg) {
  const priceMatch = msg.match(/\$?(\d+)\s*-\s*\$?(\d+)|under\s*\$?(\d+)|below\s*\$?(\d+)/i);
  if (priceMatch) {
    if (priceMatch[1] && priceMatch[2]) {
      return { min: parseInt(priceMatch[1]), max: parseInt(priceMatch[2]) };
    } else if (priceMatch[3] || priceMatch[4]) {
      return { max: parseInt(priceMatch[3] || priceMatch[4]) };
    }
  }
  return null;
}

// Generate response based on intent
function generateResponse(intents, data) {
  let response = '';

  if (intents.includes('tours') || intents.includes('featured')) {
    if (data.tours.length > 0) {
      const featuredTours = data.tours.filter(tour => tour.isFeatured || tour.featured);
      const toursToShow = featuredTours.length > 0 ? featuredTours.slice(0, 3) : data.tours.slice(0, 3);

      response += `ðŸŒŸ Here are ${featuredTours.length > 0 ? 'our featured' : 'some amazing'} tours:\n\n`;

      toursToShow.forEach((tour, index) => {
        response += `${index + 1}. **${tour.title || tour.name}**\n`;
        response += `   ðŸ“ ${tour.location}\n`;
        response += `   â±ï¸ ${tour.duration}\n`;
        response += `   ðŸ’° $${tour.price?.toLocaleString() || 'Contact for pricing'}\n`;
        if (tour.description) {
          response += `   ðŸ“ ${tour.description.substring(0, 100)}...\n`;
        }
        if (tour.isFeatured || tour.featured) {
          response += `   â­ Featured Tour!\n`;
        }
        response += `\n`;
      });

      if (data.tours.length > 3) {
        response += `ðŸ“‹ And ${data.tours.length - 3} more tours available!\n`;
      }
    } else {
      response += "ðŸ˜” No tours found. Please contact our travel experts for personalized recommendations!\n";
    }
  }

  else if (intents.includes('destinations')) {
    if (data.destinations.length > 0) {
      response += `ðŸ—ºï¸ Here are our popular destinations:\n\n`;

      data.destinations.slice(0, 5).forEach((dest, index) => {
        response += `${index + 1}. **${dest.name || dest.title}**\n`;
        response += `   ðŸ“ ${dest.location}\n`;
        if (dest.description) {
          response += `   ðŸ“ ${dest.description.substring(0, 80)}...\n`;
        }
        response += `\n`;
      });
    } else {
      response += "ðŸ—ºï¸ No destinations found. Contact us for custom destination planning!\n";
    }
  }

  else if (intents.includes('pricing')) {
    if (data.tours.length > 0) {
      const sortedTours = data.tours.sort((a, b) => (a.price || 0) - (b.price || 0));
      const budget = sortedTours.filter(t => t.price && t.price < 1000);
      const mid = sortedTours.filter(t => t.price && t.price >= 1000 && t.price < 2500);
      const luxury = sortedTours.filter(t => t.price && t.price >= 2500);

      response += `ðŸ’° **Tour Pricing Overview:**\n\n`;

      if (budget.length > 0) {
        response += `ðŸ·ï¸ **Budget Tours (Under $1,000):** ${budget.length} options\n`;
        response += `   Starting from $${Math.min(...budget.map(t => t.price))}\n\n`;
      }

      if (mid.length > 0) {
        response += `ðŸŽ¯ **Mid-Range Tours ($1,000-$2,500):** ${mid.length} options\n`;
        const avgPrice = Math.round(mid.reduce((sum, t) => sum + t.price, 0) / mid.length);
        response += `   Average price: $${avgPrice.toLocaleString()}\n\n`;
      }

      if (luxury.length > 0) {
        response += `ðŸ’Ž **Luxury Tours ($2,500+):** ${luxury.length} options\n`;
        response += `   Premium experiences from $${Math.min(...luxury.map(t => t.price)).toLocaleString()}\n\n`;
      }

      response += `**ï¿½ Best Value Tours:**\n`;
      sortedTours.slice(0, 3).forEach((tour, i) => {
        response += `${i + 1}. ${tour.title || tour.name} - $${tour.price?.toLocaleString() || 'TBD'} (${tour.duration || 'Contact us'})\n`;
      });
    }
  }

  else {
    response += `ðŸ‘‹ Welcome to TravelEase! I'm your AI travel assistant.\n\n`;
    response += `I can help you with:\n`;
    response += `â€¢ ðŸŽ’ Tours and travel packages\n`;
    response += `â€¢ ðŸ—ºï¸ Destination information\n`;
    response += `â€¢ ðŸ’° Pricing and budget planning\n`;
    response += `â€¢ â­ Featured and popular tours\n\n`;
    response += `Try asking:\n`;
    response += `â€¢ "Show me featured tours"\n`;
    response += `â€¢ "What destinations do you offer?"\n`;
    response += `â€¢ "Tours under $2000"\n`;
    response += `â€¢ "Popular destinations in Europe"`;
  }

  return response;
}

// Main processing
const userIntents = detectIntent(message);
const priceRange = extractPriceRange(message);

// Filter tours by price if specified
let filteredTours = tours;
if (priceRange && tours.length > 0) {
  filteredTours = tours.filter(tour => {
    if (!tour.price) return true;
    if (priceRange.min && priceRange.max) {
      return tour.price >= priceRange.min && tour.price <= priceRange.max;
    } else if (priceRange.max) {
      return tour.price <= priceRange.max;
    }
    return true;
  });
}

const responseText = generateResponse(userIntents, {
  tours: filteredTours,
  destinations: destinations
});

return {
  response: responseText,
  intent: userIntents,
  message_received: inputData.message,
  timestamp: new Date().toISOString(),
  query_results: {
    tours_found: tours.length,
    destinations_found: destinations.length,
    filtered_tours: filteredTours.length
  },
  status: 'success'
};
